entry.content:
  ##前言： 很多时候总会有莫名其面的账号注册，对于这种僵尸用户，我们当然是不欢迎的，就必须采取方案限制注册！ ##方案： 那怎么办呢？给注册加上人机验证就可以了！把以下代码插入主题的functions.php文件的末尾就可实现了
这里提供两断代码。 ###防止机器人恶意注册代码
新用户注册随机数学验证码//WordPress新用户注册随机数学验证码function add_security_question_fields() {//获取两个随机数, 范围0~9$num1=rand(1,9);$num2=rand(2,9);//最终网页中的具体内容echo &quot;&lt;p&gt;&lt;label for='math' class='small'&gt;验证码：$num1 + $num2 = ? &lt;/label&gt;&lt;input type='text' name='sum' class='input' value='' size='25'&gt;&quot;.&quot;&lt;input type='hidden' name='num1' value='$num1'&gt;&quot;.&quot;&lt;input type='hidden' name='num2' value='$num2'&gt;&lt;/p&gt;&quot;;}add_action('register_form','add_security_question_fields');add_action( 'register_post', 'add_security_question_validate', 10, 3 );function add_security_question_validate( $sanitized_user_login, $user_email, $errors) {$sum=$_POST['sum'];//用户提交的计算结果switch($sum){//得到正确的计算结果则直接跳出case $_POST['num1']+$_POST['num2']:break;//未填写结果时的错误讯息case null:wp_die('错误：请输入验证码！');break;//计算错误时的错误讯息default:wp_die('错误：验证码错误,请重试！');}}add_action( 'add_security_question','register_form' );####芝麻开门代码
/*注册验证*/function rand_reg_question(){$register_number=rand(0,1); // 设置随机数的返回范围$_SESSION['register_number']=$register_number;}add_action('login_head','rand_reg_question');global $register_questions;global $register_answers;// 添加问题数组$register_questions=array('中国首都在哪里？','1+1+1等于多少？');// 添加答案数组（与上面的问题对应）$register_answers=array('北京','3');add_action( 'register_form', 'add_security_question' );function add_security_question() {global $register_questions;$register_number=$_SESSION['register_number'];?&gt;&lt;p&gt;&lt;label&gt;&lt;?php echo $register_questions[$register_number];?&gt;&lt;br /&gt;&lt;input type=&quot;text&quot; name=&quot;user_proof&quot; id=&quot;user_proof&quot; class=&quot;input&quot; size=&quot;25&quot; tabindex=&quot;20&quot; /&gt;&lt;/label&gt;&lt;/p&gt;&lt;?php }add_action( 'register_post', 'add_security_question_validate', 10, 3 );function add_security_question_validate( $sanitized_user_login, $user_email, $errors) {global $register_answers;$register_number=$_SESSION['register_number'];if (!isset($_POST[ 'user_proof' ]) || empty($_POST[ 'user_proof' ])) {return $errors-&gt;add( 'proofempty', '&lt;strong&gt;错误&lt;/strong&gt;: 您还没有回答问题。' );} elseif ( strtolower( $_POST[ 'user_proof' ] ) != $register_answers[$register_number] ) {return $errors-&gt;add( 'prooffail', '&lt;strong&gt;错误&lt;/strong&gt;: 您的回答不正确。' );}}
